import React, { useState, useEffect } from 'react';
import authServices from '../services/authServices';

const Profile = () => {
    const [user, setUser] = useState({});
    const [formData, setFormData] = useState({
        coor_fname: '',
        coor_lname: '',
        coor_username: '',
        coor_email: '',
        coor_contact: '',
        coor_address: '',
        coor_role: ''
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const currentUser = authServices.getCurrentUser();
        console.log('Retrieved User:', currentUser); // Debugging line to check retrieved user

        if (currentUser) {
            setUser(currentUser);
            setFormData({
                coor_fname: currentUser.coor_fname || '',
                coor_lname: currentUser.coor_lname || '',
                coor_username: currentUser.coor_username || '',
                coor_email: currentUser.coor_email || '',
                coor_contact: currentUser.coor_contact || '',
                coor_address: currentUser.coor_address || '',
                coor_role: currentUser.coor_role || ''
            });
        }
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
        setErrorMessage(''); // Clear error message on change
        setSuccessMessage(''); // Clear success message on change
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true); // Set loading state to true
        setErrorMessage(''); // Clear previous error messages
        setSuccessMessage(''); // Clear previous success messages
    
        try {
            // Ensure the token is set in headers before making the API call
            const token = localStorage.getItem('token');
            console.log('Using Token:', token); // Debugging line
    
            if (token) {
                authServices.setAuthToken(token); // Set the token for axios requests
            }
    
            // Attempt to update the profile
            const updatedUser = await authServices.updateProfile(user.id, formData);
            console.log("Profile updated successfully:", updatedUser);
            setUser(updatedUser); // Update local user state with new data
            setSuccessMessage('Profile updated successfully!'); // Set success message
            setFormData({ ...formData }); // Reset formData or any specific fields if necessary
        } catch (error) {
            console.error("Error updating profile:", error);
            setErrorMessage(error.response?.data?.message || "An error occurred. Please try again."); // Improved error handling
        } finally {
            setLoading(false); // Set loading state back to false
        }
    };

    return (
        <div>
            <h2>Profile</h2>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>} {/* Display success message */}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>First Name:</label>
                    <input type="text" name="coor_fname" value={formData.coor_fname} onChange={handleChange} required />
                </div>
                <div>
                    <label>Last Name:</label>
                    <input type="text" name="coor_lname" value={formData.coor_lname} onChange={handleChange} />
                </div>
                <div>
                    <label>Username:</label>
                    <input type="text" name="coor_username" value={formData.coor_username} onChange={handleChange} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="coor_email" value={formData.coor_email} onChange={handleChange} required />
                </div>
                <div>
                    <label>Contact:</label>
                    <input type="text" name="coor_contact" value={formData.coor_contact} onChange={handleChange} />
                </div>
                <div>
                    <label>Address:</label>
                    <textarea name="coor_address" value={formData.coor_address} onChange={handleChange} />
                </div>
                {user.coor_role === 'Admin' && (
                    <div>
                        <label>Role:</label>
                        <select name="coor_role" value={formData.coor_role} onChange={handleChange}>
                            <option value="Admin">Admin</option>
                            <option value="Coordinator">Coordinator</option>
                        </select>
                    </div>
                )}
                <button type="submit" disabled={loading}>
                    {loading ? 'Updating...' : 'Update Profile'} {/* Show loading state */}
                </button>
            </form>
        </div>
    );
};

export default Profile;
