// src/components/PrivateRoutes.jsx
import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Assuming you have an AuthContext to get user details

const PrivateRoute = ({ requiredRole }) => {
  const { user } = useAuth(); // Get user from AuthContext

  // Check if user is authenticated and has the required role
  const isAuthorized = user && (!requiredRole || user.coor_role === requiredRole);

  // If the user is authorized, render the child components (Outlet). Otherwise, redirect to login or other route.
  return isAuthorized ? <Outlet /> : <Navigate to="/login" />;
};

export default PrivateRoute;
