import React, { useState, useEffect } from 'react';
import authServices from '../services/authServices';

const Profile = () => {
    const [user, setUser] = useState({});
    const [formData, setFormData] = useState({
        coor_fname: '',
        coor_lname: '',
        coor_username: '',
        coor_email: '',
        coor_contact: '',
        coor_address: '',
        coor_role: '',
        current_password: '',
        new_password: '',
        confirm_new_password: ''
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            setErrorMessage('No token found. Please log in again.');
            return;
        }

        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        const currentTime = Math.floor(Date.now() / 1000);
        if (decodedToken.exp < currentTime) {
            setErrorMessage('Your session has expired. Please refresh the page or log in again.');
            return;
        }

        const currentUser = authServices.getCurrentUser();
        if (currentUser && currentUser.coordinator) {
            setUser(currentUser.coordinator);
            setFormData({
                ...currentUser.coordinator,
                current_password: '',
                new_password: '',
                confirm_new_password: ''
            });
        }
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
        setErrorMessage('');
        setSuccessMessage('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setErrorMessage('');
        setSuccessMessage('');

        const { new_password, confirm_new_password } = formData;

        // Validate that new passwords match
        if (new_password && new_password !== confirm_new_password) {
            setErrorMessage('New passwords do not match.');
            setLoading(false);
            return;
        }

        const token = localStorage.getItem('token');
        if (!token) {
            setErrorMessage('No token found. Please log in again.');
            setLoading(false);
            return;
        }

        try {
            // Send update request
            const response = await authServices.updateProfile(user.id, {
                ...formData,
                coor_password: new_password || undefined, // Send new password only if it's provided
            });
            setUser(response);
            localStorage.setItem('user', JSON.stringify({ coordinator: response }));
            setSuccessMessage('Profile updated successfully!');
            setTimeout(() => setSuccessMessage(''), 3000);
        } catch (error) {
            const errorMsg = error.response?.data?.message || error.message || 'An error occurred. Please try again.';
            setErrorMessage(errorMsg);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>Profile</h2>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>First Name:</label>
                    <input type="text" name="coor_fname" value={formData.coor_fname} onChange={handleChange} required />
                </div>
                <div>
                    <label>Last Name:</label>
                    <input type="text" name="coor_lname" value={formData.coor_lname} onChange={handleChange} />
                </div>
                <div>
                    <label>Username:</label>
                    <input type="text" name="coor_username" value={formData.coor_username} onChange={handleChange} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="coor_email" value={formData.coor_email} onChange={handleChange} required />
                </div>
                <div>
                    <label>Contact:</label>
                    <input type="text" name="coor_contact" value={formData.coor_contact} onChange={handleChange} />
                </div>
                <div>
                    <label>Address:</label>
                    <textarea name="coor_address" value={formData.coor_address} onChange={handleChange} />
                </div>
                <div>
                    <label>Current Password:</label>
                    <input type="password" name="current_password" value={formData.current_password} onChange={handleChange} required />
                </div>
                <div>
                    <label>New Password:</label>
                    <input type="password" name="new_password" value={formData.new_password} onChange={handleChange} />
                </div>
                <div>
                    <label>Confirm New Password:</label>
                    <input type="password" name="confirm_new_password" value={formData.confirm_new_password} onChange={handleChange} />
                </div>
                {user.coor_role === 'Admin' && (
                    <div>
                        <label>Role:</label>
                        <select name="coor_role" value={formData.coor_role} onChange={handleChange}>
                            <option value="Admin">Admin</option>
                            <option value="Coordinator">Coordinator</option>
                        </select>
                    </div>
                )}
                <button type="submit" disabled={loading}>
                    {loading ? 'Updating...' : 'Update Profile'}
                </button>
            </form>
        </div>
    );
};

export default Profile;
