import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext'; // Correct path
import { login as loginService } from '../services/authServices'; // Correct path

const Login = () => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const { login } = useAuth(); // Get login method from context

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            const response = await loginService(identifier, password);
            console.log('Login successful:', response);
            // Use login method from context to set user and token
            login(response.user, response.jwt);
            // Redirect to dashboard or show success message
            window.location.href = '/dashboard'; // or use history.push('/dashboard')
        } catch (error) {
            console.error('Login failed:', error.message);
            // Show an error message to the user
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                value={identifier} 
                onChange={(e) => setIdentifier(e.target.value)} 
                placeholder="Username or Email" 
                required 
            />
            <input 
                type="password" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
                placeholder="Password" 
                required 
            />
            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
