import React, { useEffect, useState } from 'react';
import authServices from '../services/authServices';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const currentUser = authServices.getCurrentUser();
        if (currentUser) {
          const profileData = await authServices.getProfile(currentUser.id);
          setUser(profileData);
        }
      } catch (error) {
        setError('Failed to fetch profile data.');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  const handleUpdate = async () => {
    try {
      const updatedUser = await authServices.updateProfile(user.id, user);
      setUser(updatedUser);
      alert('Profile updated successfully!');
    } catch (error) {
      setError('Failed to update profile.');
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      <h2>User Profile</h2>
      <p>First Name: {user.coor_fname}</p>
      <p>Last Name: {user.coor_lname}</p>
      <p>Email: {user.coor_email}</p>
      {/* Include other profile fields and an update form here */}
      <button onClick={handleUpdate}>Update Profile</button>
    </div>
  );
};

export default Profile;
