// src/context/AuthContext.jsx
import React, { createContext, useContext, useState, useMemo } from 'react';
import authServices from '../services/authServices';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(() => {
        try {
            const storedUserData = localStorage.getItem('user');
            console.log("Stored User Data in localStorage:", storedUserData); // Log for debugging
            return storedUserData ? JSON.parse(storedUserData) : null;
        } catch (error) {
            console.error("Failed to parse user data from local storage:", error);
            return null;
        }
    });

    const [token, setToken] = useState(() => {
        try {
            return localStorage.getItem('token') || null;
        } catch (error) {
            console.error("Failed to retrieve token from local storage:", error);
            return null;
        }
    });

    const logout = () => {
        setUser(null);
        setToken(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        console.log("User logged out"); // Log for debugging
    };

    const login = async (identifier, password) => {
        try {
            const { coordinator, token: newToken } = await authServices.login(identifier, password);
            setUser(coordinator);  // Store the coordinator object
            setToken(newToken);     // Store the token
            localStorage.setItem('token', newToken);
            localStorage.setItem('user', JSON.stringify(coordinator));
            console.log("User logged in:", coordinator); // Log for debugging
        } catch (error) {
            console.error("Login failed:", error);
            throw error; // Consider providing user feedback instead
        }
    };

    // Memoize context value to prevent unnecessary re-renders
    const contextValue = useMemo(() => ({
        user,
        token,
        login,
        logout,
    }), [user, token]);

    return (
        <AuthContext.Provider value={contextValue}>
            {children}
        </AuthContext.Provider>
    );
};

// Hook to use the AuthContext
export const useAuth = () => useContext(AuthContext);
