// src/context/AuthContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';
import authServices from '../services/authServices';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(() => {
        try {
            const storedUserData = localStorage.getItem('user');
            console.log("Stored User Data in localStorage:", storedUserData); // Log for debugging
            return storedUserData ? JSON.parse(storedUserData) : null;
        } catch (error) {
            console.error("Failed to parse user data from local storage:", error);
            return null;
        }
    });

    const isTokenExpired = () => {
        const token = localStorage.getItem('token');
        if (token) {
            const decodedToken = JSON.parse(atob(token.split('.')[1]));
            const currentTime = Math.floor(Date.now() / 1000); // Current time in seconds
            return decodedToken.exp < currentTime;
        }
        return true; // If no token, consider it expired
    };
    
    // Inside your updateProfile or login logic, add this check
    if (isTokenExpired()) {
        logout();
        alert("Session expired. Please log in again.");
        return;
    }

    const login = async (identifier, password) => {
        try {
            const { user, token } = await authServices.login(identifier, password);
            setUser(user);
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));
            console.log("User logged in:", user); // Log for debugging
        } catch (error) {
            console.error("Login failed:", error);
            throw error;
        }
    };

    const logout = () => {
        setUser(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        console.log("User logged out"); // Log for debugging
    };

    return (
        <AuthContext.Provider value={{ user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
