import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext'; // Make sure this is correctly imported
import userServices from '../services/userServices';

const ManageUsers = () => {
    const { token } = useAuth(); // Get token from AuthContext
    const [users, setUsers] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    const fetchUsers = async () => {
        try {
            const usersData = await userServices.getAllUsers(token);
            setUsers(usersData);
        } catch (error) {
            setErrorMessage(`Error fetching users: ${error.message}`);
            console.error(error);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, [token]); // Fetch users whenever the token changes

    return (
        <div>
            <h2>Manage Users</h2>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            <ul>
                {users.map(user => (
                    <li key={user.id}>{user.coor_fname} {user.coor_lname}</li>
                ))}
            </ul>
        </div>
    );
};

export default ManageUsers;
