// src/context/AuthContext.jsx
import React, { createContext, useContext } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const login = async (identifier, password) => {
        try {
            const response = await axios.post('http://localhost:1337/api/coordinators/login', {
                identifier,
                password,
            });
            console.log("Response from API:", response.data);
            const { coordinator, token } = response.data;

            if (!token) {
                throw new Error('Login failed, no token received.');
            }

            localStorage.setItem('user', JSON.stringify(coordinator));
            localStorage.setItem('token', token);

            return response.data;
        } catch (error) {
            const errorMessage = error.response?.data?.error?.message || 'Error logging in. Please check your credentials';
            console.error("Error during login:", errorMessage);
            throw new Error(errorMessage);
        }
    };

    return (
        <AuthContext.Provider value={{ login }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
