import React, { useEffect, useState } from 'react';
import authServices from '../services/authServices'; // Ensure you have services for managing users

const ManageUsers = () => {
    const [coordinators, setCoordinators] = useState([]);
    const [newCoordinator, setNewCoordinator] = useState({ coor_fname: '', coor_lname: '', coor_role: '' });

    useEffect(() => {
        const fetchCoordinators = async () => {
            try {
                const response = await authServices.getAllCoordinators(); // Ensure this function exists in your authServices
                setCoordinators(response.data);
            } catch (error) {
                console.error("Error fetching coordinators:", error);
            }
        };

        fetchCoordinators();
    }, []);

    const handleAddCoordinator = async () => {
        try {
            const response = await authServices.addCoordinator(newCoordinator); // Ensure this function exists in your authServices
            setCoordinators([...coordinators, response.data]);
            setNewCoordinator({ coor_fname: '', coor_lname: '', coor_role: '' }); // Reset the form
        } catch (error) {
            console.error("Error adding coordinator:", error);
        }
    };

    return (
        <div>
            <h1>Manage Coordinators</h1>
            <form onSubmit={e => { e.preventDefault(); handleAddCoordinator(); }}>
                <input
                    type="text"
                    placeholder="First Name"
                    value={newCoordinator.coor_fname}
                    onChange={e => setNewCoordinator({ ...newCoordinator, coor_fname: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Last Name"
                    value={newCoordinator.coor_lname}
                    onChange={e => setNewCoordinator({ ...newCoordinator, coor_lname: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Role"
                    value={newCoordinator.coor_role}
                    onChange={e => setNewCoordinator({ ...newCoordinator, coor_role: e.target.value })}
                    required
                />
                <button type="submit">Add Coordinator</button>
            </form>

            <h2>Current Coordinators</h2>
            <ul>
                {coordinators.map(coordinator => (
                    <li key={coordinator.id}>{coordinator.coor_fname} {coordinator.coor_lname} ({coordinator.coor_role})</li>
                ))}
            </ul>
        </div>
    );
};

export default ManageUsers;
