// src/components/ManageUsers.jsx
import React, { useEffect, useState } from 'react';
import userServices from '../services/UserServices'; // Ensure this matches the correct casing
import { useAuth } from '../context/AuthContext';

const ManageUsers = () => {
    const { user } = useAuth(); // Get user from AuthContext
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [editingUserId, setEditingUserId] = useState(null);
    const [formData, setFormData] = useState({
        coor_fname: '',
        coor_lname: '',
        coor_username: '',
        coor_email: '',
        coor_contact: '',
        coor_address: '',
        coor_role: ''
    });

    useEffect(() => {
        const fetchUsers = async () => {
            setError(null); // Reset error state before fetching
            try {
                const fetchedUsers = await userServices.getAllUsers();
                console.log("Fetched Users:", fetchedUsers); // Debugging log
                setUsers(fetchedUsers);
            } catch (error) {
                console.error("Error fetching users:", error); // More detailed error logging
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        // Only fetch users if the user is an Admin
        if (user && user.coordinator && user.coordinator.coor_role === 'Admin') {
            console.log("Current User:", user); // Log user information to verify role
            fetchUsers();
        } else {
            setLoading(false); // Set loading to false if not Admin
        }
    }, [user]);

    const handleDeleteUser = async (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await userServices.deleteUser(userId);
                setUsers(users.filter((u) => u.id !== userId)); // Remove deleted user from state
            } catch (error) {
                setError(error.message);
            }
        }
    };

    const handleEditUser = (user) => {
        setEditingUserId(user.id);
        setFormData({
            coor_fname: user.coor_fname,
            coor_lname: user.coor_lname,
            coor_username: user.coor_username,
            coor_email: user.coor_email,
            coor_contact: user.coor_contact,
            coor_address: user.coor_address,
            coor_role: user.coor_role
        });
    };

    const handleUpdateUser = async (e) => {
        e.preventDefault();
        try {
            const updatedUser = await userServices.updateUser(editingUserId, formData); // Assuming this service method exists
            setUsers(users.map(user => user.id === editingUserId ? updatedUser : user)); // Update the user's data in state
            setEditingUserId(null); // Reset editing state
        } catch (error) {
            setError(error.message);
        }
    };

    if (loading) return <p>Loading users...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div className="manage-users">
            <h2>Manage Users</h2>
            {users.length === 0 ? (
                <p>No users found.</p>
            ) : (
                <>
                    {editingUserId && (
                        <form onSubmit={handleUpdateUser}>
                            <h3>Edit User</h3>
                            <div>
                                <label>First Name:</label>
                                <input type="text" name="coor_fname" value={formData.coor_fname} onChange={(e) => setFormData({ ...formData, coor_fname: e.target.value })} required />
                            </div>
                            <div>
                                <label>Last Name:</label>
                                <input type="text" name="coor_lname" value={formData.coor_lname} onChange={(e) => setFormData({ ...formData, coor_lname: e.target.value })} />
                            </div>
                            <div>
                                <label>Username:</label>
                                <input type="text" name="coor_username" value={formData.coor_username} onChange={(e) => setFormData({ ...formData, coor_username: e.target.value })} required />
                            </div>
                            <div>
                                <label>Email:</label>
                                <input type="email" name="coor_email" value={formData.coor_email} onChange={(e) => setFormData({ ...formData, coor_email: e.target.value })} required />
                            </div>
                            <div>
                                <label>Contact:</label>
                                <input type="text" name="coor_contact" value={formData.coor_contact} onChange={(e) => setFormData({ ...formData, coor_contact: e.target.value })} />
                            </div>
                            <div>
                                <label>Address:</label>
                                <textarea name="coor_address" value={formData.coor_address} onChange={(e) => setFormData({ ...formData, coor_address: e.target.value })} />
                            </div>
                            <div>
                                <label>Role:</label>
                                <select name="coor_role" value={formData.coor_role} onChange={(e) => setFormData({ ...formData, coor_role: e.target.value })}>
                                    <option value="Admin">Admin</option>
                                    <option value="Coordinator">Coordinator</option>
                                </select>
                            </div>
                            <button type="submit">Update User</button>
                            <button type="button" onClick={() => setEditingUserId(null)}>Cancel</button>
                        </form>
                    )}
                    <table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Role</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(({ id, coor_fname, coor_lname, coor_email, coor_role }) => (
                                <tr key={id}>
                                    <td>{id}</td>
                                    <td>{`${coor_fname} ${coor_lname}`}</td>
                                    <td>{coor_email}</td>
                                    <td>{coor_role}</td>
                                    <td>
                                        <button onClick={() => handleEditUser({ id, coor_fname, coor_lname, coor_email, coor_role })}>Edit</button>
                                        <button onClick={() => handleDeleteUser(id)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    );
};

export default ManageUsers;
