// src/components/ManageUsers.jsx
import React, { useEffect, useState } from 'react';
import userServices from '../services/UserServices'; // Ensure this matches the correct casing
import { useAuth } from '../context/AuthContext';

const ManageUsers = () => {
    const { user } = useAuth(); // Get user from AuthContext
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            setError(null); // Reset error state before fetching
            try {
                const fetchedUsers = await userServices.getAllUsers();
                setUsers(fetchedUsers);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        // Only fetch users if the user is an Admin
        if (user && user.coordinator && user.coordinator.coor_role === 'Admin') {
            fetchUsers();
        } else {
            setLoading(false); // Set loading to false if not Admin
        }
    }, [user]);

    const handleDeleteUser = async (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await userServices.deleteUser(userId);
                setUsers(users.filter((u) => u.id !== userId)); // Remove deleted user from state
            } catch (error) {
                setError(error.message);
            }
        }
    };

    const handleRoleChange = async (userId, newRole) => {
        try {
            const updatedUser = await userServices.updateUserRole(userId, newRole); // Assuming this service method exists
            setUsers(users.map(user => user.id === userId ? updatedUser : user)); // Update the user's role in state
        } catch (error) {
            setError(error.message);
        }
    };

    if (loading) return <p>Loading users...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div className="manage-users">
            <h2>Manage Users</h2>
            {users.length === 0 ? (
                <p>No users found.</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(({ id, coor_fname, coor_lname, coor_email, coor_role }) => (
                            <tr key={id}>
                                <td>{id}</td>
                                <td>{`${coor_fname} ${coor_lname}`}</td>
                                <td>{coor_email}</td>
                                <td>
                                    <select 
                                        value={coor_role} 
                                        onChange={(e) => handleRoleChange(id, e.target.value)}
                                    >
                                        <option value="Admin">Admin</option>
                                        <option value="Coordinator">Coordinator</option>
                                    </select>
                                </td>
                                <td>
                                    <button onClick={() => handleDeleteUser(id)}>Delete</button>
                                    {/* Add more actions if needed */}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default ManageUsers;
