import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authServices from '../services/authServices';

const Login = () => {
  const [credentials, setCredentials] = useState({
    identifier: '', // Unified identifier for username or email
    coor_password: '', // Ensure this matches your backend
  });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await authServices.login(credentials.identifier, credentials.coor_password);
      
      // Store the complete user data including token
      localStorage.setItem('user', JSON.stringify(response)); // Store entire response
      
      navigate('/dashboard'); // Redirect to dashboard after successful login
      window.location.reload(); // Optional: reload page to refresh user data
    } catch (error) {
      setError(error.message); // Display the error message
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="identifier" // This remains as identifier
          value={credentials.identifier}
          onChange={handleChange}
          placeholder="Username or Email"
          required
        />
        <input
          type="password"
          name="coor_password" // Ensure this matches the backend expectation
          value={credentials.coor_password}
          onChange={handleChange}
          placeholder="Password"
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
