// src/context/AuthContext.jsx
import React, { createContext, useContext, useState } from 'react';
import authServices from '../services/authServices';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(() => {
      console.log("User data from local storage:", userData);

        // Get user from local storage on initial load
        const userData = localStorage.getItem('user');
        // Only parse if userData is a valid string
        return userData ? JSON.parse(userData) : null; // Parse user data if exists
    });

    // Login function
    const login = async (identifier, password) => {
        try {
            const { user, token } = await authServices.login(identifier, password);
            setUser(user); // Set user data in state
            localStorage.setItem('token', token); // Store token in local storage
            localStorage.setItem('user', JSON.stringify(user)); // Store user in local storage
        } catch (error) {
            console.error("Login failed:", error);
            throw error; // Rethrow the error for handling in the component
        }
    };

    // Logout function
    const logout = () => {
        setUser(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user'); // Remove user from local storage
    };

    return (
        <AuthContext.Provider value={{ user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
