import axios from 'axios';

const API_URL = 'http://localhost:1337/api'; // Replace with your Strapi backend URL

const setAuthToken = (token) => {
  if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  } else {
      delete axios.defaults.headers.common['Authorization'];
  }
};

const token = localStorage.getItem('token');
setAuthToken(token);

// Register a new coordinator
export const signup = async (formData) => {
  try {
    const response = await axios.post(`${API_URL}/coordinators/register`, { data: formData });
    if (response.data.jwt) {
      localStorage.setItem('user', JSON.stringify(response.data));
    }
    return response.data;
  } catch (error) {
      console.error('Signup error:', error); // Log the entire error object
      // Ensure to access the error response and get a meaningful message
      const errorMessage = error.response?.data?.error?.message || 'Signup failed. Please try again.';
      throw new Error(errorMessage); // Ensure this throws a string message
    }
};


// Login a user (coordinator or admin)
export const login = async (identifier, password) => {
  try {
      const response = await axios.post(`${API_URL}/coordinators/login`, { identifier, password });
      if (response.data.jwt) {
          localStorage.setItem('user', JSON.stringify(response.data)); // Store user data
      }
      return response.data; // Return user data and token
  } catch (error) {
      console.error('Login error:', error); // Log the entire error object
      const errorMessage = error.response?.data?.error?.message || 'Login failed. Please try again.';
      throw new Error(errorMessage); // Throw a meaningful error message
  }
};


// Logout a user by removing the token from localStorage
export const logout = () => {
  localStorage.removeItem('user');
};

// Get the current authenticated user from localStorage
export const getCurrentUser = () => {
  const user = JSON.parse(localStorage.getItem('user')); // Retrieve the user object
  return user; // Return user object which includes JWT
};

// Check if a user is authenticated by checking the presence of a token
export const isAuthenticated = () => {
  const user = getCurrentUser();
  return user && user.jwt ? true : false;
};

// Get the user profile by ID
export const getProfile = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}/coordinators/${userId}`, {
      headers: {
        Authorization: `Bearer ${getCurrentUser().jwt}`, // Include the token in the headers
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching profile:', error);
    throw new Error('Failed to fetch profile data');
  }
};

// Update the user profile
export const updateProfile = async (userId, profileData) => {
  try {
      const response = await axios.put(`http://localhost:1337/api/coordinators/${userId}`, profileData);
      return response.data; // Return the response data if the request is successful
  } catch (error) {
      console.error("Error updating profile:", error);
      throw error; // Rethrow the error for further handling
  }
};

// Export all authentication services including new ones
const authServices = {
  signup,
  login,
  logout,
  getCurrentUser,
  isAuthenticated,
  getProfile,
  updateProfile,
  setAuthToken,
};

// Optionally expose it to the global window object for console access
window.authServices = authServices;

export default authServices;
