// src/services/authServices.js
import axios from 'axios';

const API_URL = 'http://localhost:1337/api'; // Replace with your Strapi backend URL

// Register a new coordinator
export const signup = async (formData) => {
    try {
      const response = await axios.post(`${API_URL}/coordinators/register`, { data: formData });
      if (response.data.jwt) {
        localStorage.setItem('user', JSON.stringify(response.data));
      }
      return response.data;
    } catch (error) {
        console.error('Signup error:', error); // Log the entire error object
        // Ensure to access the error response and get a meaningful message
        const errorMessage = error.response?.data?.error?.message || 'Signup failed. Please try again.';
        throw new Error(errorMessage); // Ensure this throws a string message
      }
};

// Login a user (coordinator or admin)
export const login = async (identifier, password) => {
    try {
        // Log the identifier and password for debugging
        console.log("Logging in with:", { identifier, password });

        // Send the login request to the Strapi API
        const response = await axios.post('http://localhost:1337/api/coordinators/login', {
            identifier,  // Use the identifier parameter directly
            password,    // Use the password parameter directly
        });

        // Log the response from the API for debugging
        console.log('Response from API:', response.data);
        const { user, jwt } = response.data;

        // Check if the JWT is present
        if (!jwt) {
            console.error('No token received from the server.');
            throw new Error('Login failed, no token received.');
        }

        // Store the user and token in localStorage
        localStorage.setItem('user', JSON.stringify(user));
        localStorage.setItem('token', jwt);

        // Return the response data for further use
        return response.data;

    } catch (error) {
        // Handle and log the error appropriately
        const errorMessage = error.response?.data?.error?.message || 'Error logging in. Please check your credentials';
        console.error("Error during login:", errorMessage);
        throw new Error(errorMessage);
    }
};



// Logout a user by removing the token from localStorage
export const logout = () => {
  localStorage.removeItem('user');
};

// Get the current authenticated user from localStorage
export const getCurrentUser = () => {
  return JSON.parse(localStorage.getItem('user'));
};

// Check if a user is authenticated by checking the presence of a token
export const isAuthenticated = () => {
  const user = getCurrentUser();
  return user && user.jwt ? true : false;
};

// Export all authentication services
const authServices = {
  signup,
  login,
  logout,
  getCurrentUser,
  isAuthenticated,
};

export default authServices;
