// src/components/ManageUsers.jsx
import React, { useState, useEffect } from 'react';
import authServices from '../services/authServices'; // Import your auth services

const ManageUsers = () => {
    const [coordinators, setCoordinators] = useState([]);
    const [formData, setFormData] = useState({
        id: '',
        coor_fname: '',
        coor_lname: '',
        coor_username: '',
        coor_email: '',
        coor_contact: '',
        coor_address: '',
        coor_role: 'Coordinator',
        coor_password: '', // Required for creating a new coordinator
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        const fetchCoordinators = async () => {
            try {
                const response = await authServices.getCoordinators(); // Fetch coordinators
                setCoordinators(response);
            } catch (error) {
                setErrorMessage('Failed to fetch coordinators.');
            }
        };

        fetchCoordinators();
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
        setErrorMessage('');
        setSuccessMessage('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErrorMessage('');
        setSuccessMessage('');

        try {
            if (formData.id) {
                // Update existing coordinator
                await authServices.updateCoordinator(formData.id, formData);
                setSuccessMessage('Coordinator updated successfully!');
            } else {
                // Create new coordinator
                await authServices.createCoordinator(formData);
                setSuccessMessage('Coordinator added successfully!');
            }
            // Refresh the coordinator list
            const response = await authServices.getCoordinators();
            setCoordinators(response);
            setFormData({
                id: '',
                coor_fname: '',
                coor_lname: '',
                coor_username: '',
                coor_email: '',
                coor_contact: '',
                coor_address: '',
                coor_role: 'Coordinator',
                coor_password: '',
            });
        } catch (error) {
            setErrorMessage('Error while saving coordinator.');
        }
    };

    const handleEdit = (coordinator) => {
        setFormData(coordinator);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this coordinator?')) {
            try {
                await authServices.deleteCoordinator(id);
                setCoordinators(coordinators.filter(coor => coor.id !== id));
                setSuccessMessage('Coordinator deleted successfully!');
            } catch (error) {
                setErrorMessage('Error while deleting coordinator.');
            }
        }
    };

    return (
        <div>
            <h2>Manage Coordinators</h2>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}

            <form onSubmit={handleSubmit}>
                <input type="hidden" name="id" value={formData.id} />
                <div>
                    <label>First Name:</label>
                    <input type="text" name="coor_fname" value={formData.coor_fname} onChange={handleChange} required />
                </div>
                <div>
                    <label>Last Name:</label>
                    <input type="text" name="coor_lname" value={formData.coor_lname} onChange={handleChange} />
                </div>
                <div>
                    <label>Username:</label>
                    <input type="text" name="coor_username" value={formData.coor_username} onChange={handleChange} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="coor_email" value={formData.coor_email} onChange={handleChange} required />
                </div>
                <div>
                    <label>Contact:</label>
                    <input type="text" name="coor_contact" value={formData.coor_contact} onChange={handleChange} />
                </div>
                <div>
                    <label>Address:</label>
                    <textarea name="coor_address" value={formData.coor_address} onChange={handleChange} />
                </div>
                <div>
                    <label>Role:</label>
                    <select name="coor_role" value={formData.coor_role} onChange={handleChange}>
                        <option value="Coordinator">Coordinator</option>
                        <option value="Admin">Admin</option>
                    </select>
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="coor_password" value={formData.coor_password} onChange={handleChange} required={formData.id === ''} />
                </div>
                <button type="submit">{formData.id ? 'Update Coordinator' : 'Add Coordinator'}</button>
            </form>

            <h3>Existing Coordinators</h3>
            <ul>
                {coordinators.map(coor => (
                    <li key={coor.id}>
                        {coor.coor_fname} {coor.coor_lname} - {coor.coor_email}
                        <button onClick={() => handleEdit(coor)}>Edit</button>
                        <button onClick={() => handleDelete(coor.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ManageUsers;
