// src/services/UserServices.js
import axios from 'axios';
import authServices from '../services/authServices';  // For retrieving the token

const API_URL = 'http://localhost:1337/api/coordinators';  // Base URL for coordinators

// Fetch all users (coordinators/admins)
const getAllUsers = async () => {
    try {
      const user = authServices.getCurrentUser(); // Get user from localStorage
      
      if (!user || !user.token) {
        throw new Error('User not authenticated'); // Check if token exists
      }
  
      return response.data;
    } catch (error) {
      console.error('Error fetching users:', error.response?.data || error.message);
      throw new Error(error.response?.data?.message || 'Failed to fetch users');
    }
  };

// Fetch a single user by ID
const getUserById = async (userId) => {
  try {
    const token = authServices.getCurrentUser().jwt;
    const response = await axios.get(`${API_URL}/${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching user:', error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to fetch user');
  }
};

// Create a new user (admin/coordinator)
const createUser = async (userData) => {
  try {
    const token = authServices.getCurrentUser().jwt;
    const response = await axios.post(API_URL, { data: userData }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error creating user:', error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to create user');
  }
};

// Update a user by ID
const updateUser = async (userId, userData) => {
  try {
    const token = authServices.getCurrentUser().jwt;
    const response = await axios.put(`${API_URL}/${userId}`, { data: userData }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error updating user:', error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to update user');
  }
};

// Delete a user by ID
const deleteUser = async (userId) => {
  try {
    const token = authServices.getCurrentUser().jwt;
    const response = await axios.delete(`${API_URL}/${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error deleting user:', error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to delete user');
  }
};

// Assign object to a variable first
const userServices = {
  getAllUsers,
  getUserById,
  createUser,
  updateUser,
  deleteUser,
};

// Then export the variable
export default userServices;
