import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  // State variables to hold form data and error messages
  const [formData, setFormData] = useState({
    identifier: '',
    password: '',
  });
  const [error, setError] = useState('');

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior
    setError(''); // Reset error message

    try {
      // Send login request
      const response = await axios.post('http://localhost:1337/api/coordinators/login', {
        identifier: formData.identifier,
        password: formData.password,
      });

      // Handle successful response
      if (response.data && response.data.token) {
        console.log('Login successful:', response.data);
        // Store token in local storage or state management
        localStorage.setItem('token', response.data.token);
        // Redirect or perform another action after successful login
      }
    } catch (err) {
      // Handle error response
      if (err.response && err.response.data) {
        setError(err.response.data.error.message || 'Error logging in. Please check your credentials');
      } else {
        setError('An unexpected error occurred. Please try again later.');
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message */}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="identifier">Username/Email:</label>
          <input
            type="text"
            id="identifier"
            name="identifier"
            value={formData.identifier}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
