import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authServices from '../services/authServices';

const Login = () => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true); // Set loading to true on form submission
        try {
            const userData = await authServices.login(identifier, password);
            
            // Assuming the token is part of the userData returned
            const { token, ...userInfo } = userData; // Destructure to get token and user info
    
            // Store the token and user data in localStorage
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(userInfo)); // Store user data without token
    
            setSuccess('Login successful! Redirecting...');
            setError(null);
            
            setTimeout(() => {
                navigate('/dashboard'); // Redirect to dashboard or profile page
            }, 2000);
        } catch (error) {
            console.error('Error during login:', error);
            setError(error.response?.data?.message || 'An unexpected error occurred.'); // Improved error handling
            setSuccess(null);
        } finally {
            setLoading(false); // Set loading to false after submission
        }
    };
    
    return (
        <div>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Email or Username"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Logging In...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
