// src/services/authServices.js
import axios from 'axios';

const API_URL = 'http://localhost:1337/api'; // Replace with your Strapi backend URL

// Register a new coordinator
export const signup = async (formData) => {
    try {
      const response = await axios.post(`${API_URL}/coordinators/register`, {
        coor_fname: formData.coor_fname,
        coor_lname: formData.coor_lname,
        username: formData.username,
        coor_email: formData.coor_email,
        coor_password: formData.coor_password,
        coor_role: formData.coor_role,
      });
  
      // If signup is successful, store the token
      if (response.data.jwt) {
        localStorage.setItem('user', JSON.stringify(response.data));
      }
  
      return response.data;
    } catch (error) {
      console.error('Signup error:', error.response?.data); // Log the detailed error
      throw error.response?.data?.message || 'Signup failed. Please try again.'; // Extract the message or provide a fallback
    }
  };

// Login a user (coordinator or admin)
export const login = async (identifier, password) => {
  try {
    const response = await axios.post(`${API_URL}/coordinators/login`, {
      identifier, // This can be either email or username
      password,
    });
  
    // If login is successful, store the token
    if (response.data.jwt) {
      localStorage.setItem('user', JSON.stringify(response.data));
    }
  
    return response.data;
  } catch (error) {
    console.error('Login error:', error);
    throw error.response?.data?.message || 'Login failed. Please try again.';
  }
};

// Logout a user by removing the token from localStorage
export const logout = () => {
  localStorage.removeItem('user');
};

// Get the current authenticated user from localStorage
export const getCurrentUser = () => {
  return JSON.parse(localStorage.getItem('user'));
};

// Check if a user is authenticated by checking the presence of a token
export const isAuthenticated = () => {
  const user = getCurrentUser();
  return user && user.jwt ? true : false;
};

// Export all authentication services
const authServices = {
  signup,
  login,
  logout,
  getCurrentUser,
  isAuthenticated,
};

export default authServices;
