import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import authServices from '../services/authServices';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const { isAdmin } = useAuth(); // Use isAdmin from AuthContext

  useEffect(() => {
    if (!isAdmin()) {
      navigate('/'); // Redirect if not admin
    } else {
      fetchUsers(); // Fetch users if admin
    }
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await authServices.getUsers(); // Fetch users from backend
      setUsers(response);
    } catch (err) {
      setError('Failed to fetch users.');
      console.error(err);
    }
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Manage Users</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.coor_username}</td>
              <td>{user.coor_email}</td>
              <td>{user.coor_role}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageUsers;
