// src/components/ManageUsers.jsx
import React, { useEffect, useState } from 'react';
import userServices from '../services/UserServices';
import { useAuth } from '../context/AuthContext';

const ManageUsers = () => {
    const { user } = useAuth(); // Get user from AuthContext
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const fetchedUsers = await userServices.getAllUsers();
                setUsers(fetchedUsers);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        // Only fetch users if the user is an Admin
        if (user && user.coordinator && user.coordinator.coor_role === 'Admin') {
            fetchUsers();
        } else {
            setLoading(false); // Set loading to false if not Admin
        }
    }, [user]);

    const handleDeleteUser = async (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await userServices.deleteUser(userId);
                setUsers(users.filter(user => user.id !== userId)); // Remove deleted user from state
            } catch (error) {
                setError(error.message);
            }
        }
    };

    if (loading) return <p>Loading users...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div className="manage-users">
            <h2>Manage Users</h2>
            {users.length === 0 ? (
                <p>No users found.</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{`${user.coor_fname} ${user.coor_lname}`}</td>
                                <td>{user.coor_email}</td>
                                <td>{user.coor_role}</td>
                                <td>
                                    <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
                                    {/* You can add edit/update functionality here */}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default ManageUsers;
