import React, { createContext, useContext, useState } from 'react';
import authServices from '../services/authServices';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    try {
      const storedUserData = localStorage.getItem('user');
      
      // Check for null, "undefined", or invalid values
      if (!storedUserData || storedUserData === "undefined") {
        return null; // No user found, return null
      }

      return JSON.parse(storedUserData); // Parse the user data if it's valid JSON
    } catch (error) {
      console.error('Failed to parse user data from local storage:', error);
      localStorage.removeItem('user'); // Clear out the corrupted user data
      return null; // Return null if there's an error in parsing
    }
  });

  const login = async (identifier, password) => {
    try {
      const { user, token } = await authServices.login(identifier, password);
      setUser(user); // Set user data in state
      localStorage.setItem('token', token); // Store token in local storage
      localStorage.setItem('user', JSON.stringify(user)); // Store user in local storage
    } catch (error) {
      console.error('Login failed:', error);
      throw error; // Rethrow the error for handling in the component
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user'); // Remove user from local storage
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
