import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import authServices from '../services/authServices';

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const fetchedUsers = await authServices.fetchUsers(); // This should be implemented in authServices
                setUsers(fetchedUsers);
            } catch (err) {
                setError('Failed to fetch users.');
            } finally {
                setLoading(false);
            }
        };

        fetchUsers();
    }, []);

    const handleDelete = async (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await authServices.deleteUser(userId); // This should be implemented in authServices
                setUsers(users.filter(user => user.id !== userId)); // Update local state
            } catch (err) {
                setError('Failed to delete user.');
            }
        }
    };

    return (
        <div>
            <h1>Manage Users</h1>
            {loading && <p>Loading users...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Username</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {!loading && users.length > 0 ? (
                        users.map(user => (
                            <tr key={user.id}>
                                <td>{user.coor_fname}</td>
                                <td>{user.coor_lname}</td>
                                <td>{user.coor_email}</td>
                                <td>{user.coor_username}</td>
                                <td>
                                    <Link to={`/edit-user/${user.id}`}>Edit</Link>
                                    <button onClick={() => handleDelete(user.id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="5">No users found</td>
                        </tr>
                    )}
                </tbody>
            </table>
            <Link to="/create-user">Create New User</Link>
        </div>
    );
};

export default ManageUsers;
