// src/components/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authServices from '../services/authServices';

const Login = () => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // For navigation

    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent form submission
        try {
            const { user, token } = await authServices.login(identifier, password);
            console.log('Retrieved User:', user);
            // Store user in context or local storage if needed
            // Redirect to the desired page after successful login
            navigate('/profile'); // Redirect to profile or dashboard
            window.location.reload();
        } catch (error) {
            console.error("Login failed:", error.message);
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Email or Username"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required // Added required attribute
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required // Added required attribute
                />
                <button type="submit">Login</button>
            </form>
            <p>
                Don't have an account?{' '}
                <button onClick={() => navigate('/signup')} style={{ background: 'none', border: 'none', color: 'blue', cursor: 'pointer', textDecoration: 'underline' }}>
                    Sign Up
                </button>
            </p>
        </div>
    );
};

export default Login;
