// src/services/authServices.js
export const login = async (identifier, password) => {
    try {
        const response = await axios.post(`${API_URL}/coordinators/login`, { identifier, password });
        if (response.data.jwt) {
            localStorage.setItem('user', JSON.stringify(response.data)); // Store user data
        }
        return response.data; // Return user data and token
    } catch (error) {
        console.error('Login error:', error); // Log the entire error object
        const errorMessage = error.response?.data?.error?.message || 'Login failed. Please try again.';
        throw new Error(errorMessage); // Throw a meaningful error message
    }
};

// src/components/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authServices from '../services/authServices';

const Login = () => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true); // Set loading to true on form submission
        try {
            const userData = await authServices.login(identifier, password);
            setSuccess('Login successful! Redirecting...');
            setError(null);
            localStorage.setItem('user', JSON.stringify(userData)); // Store user data
            setTimeout(() => {
                navigate('/profile'); // Redirect to profile page
            }, 2000);
        } catch (error) {
            console.error('Error during login:', error);
            setError(error.message || 'An unexpected error occurred.');
            setSuccess(null);
        } finally {
            setLoading(false); // Set loading to false after submission
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Email or Username"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Logging In...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
