import React, { useState, useEffect } from 'react';
import authServices from '../services/authServices';

const Profile = () => {
    const [user, setUser] = useState({});
    const [formData, setFormData] = useState({
        coor_fname: '',
        coor_lname: '',
        coor_username: '',
        coor_email: '',
        coor_contact: '',
        coor_address: '',
        coor_role: ''
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const [currentPassword, setCurrentPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [passwordSuccessMessage, setPasswordSuccessMessage] = useState('');
    const [passwordErrorMessage, setPasswordErrorMessage] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            setErrorMessage('No token found. Please log in again.');
            return;
        }

        try {
            const decodedToken = JSON.parse(atob(token.split('.')[1]));
            const currentTime = Math.floor(Date.now() / 1000);

            if (decodedToken.exp < currentTime) {
                setErrorMessage('Your session has expired. Please refresh the page or log in again.');
                return;
            }
        } catch (error) {
            setErrorMessage('Invalid token. Please log in again.');
            return;
        }

        const currentUser = authServices.getCurrentUser();
        if (currentUser && currentUser.coordinator) {
            setUser(currentUser.coordinator);
            setFormData({
                coor_fname: currentUser.coordinator.coor_fname || '',
                coor_lname: currentUser.coordinator.coor_lname || '',
                coor_username: currentUser.coordinator.coor_username || '',
                coor_email: currentUser.coordinator.coor_email || '',
                coor_contact: currentUser.coordinator.coor_contact || '',
                coor_address: currentUser.coordinator.coor_address || '',
                coor_role: currentUser.coordinator.coor_role || ''
            });
        }
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
        setErrorMessage('');
        setSuccessMessage('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setErrorMessage('');
        setSuccessMessage('');

        const token = localStorage.getItem('token');
        if (!token) {
            setErrorMessage('No token found. Please log in again.');
            setLoading(false);
            return;
        }

        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        const currentTime = Math.floor(Date.now() / 1000);
        if (decodedToken.exp < currentTime) {
            setErrorMessage('Your session has expired. Please refresh the page or log in again.');
            setLoading(false);
            return;
        }

        try {
            const response = await authServices.updateProfile(user.id, formData);
            setUser(response);
            const userdata = {
                coordinator: response
            };
            localStorage.setItem('user', JSON.stringify(userdata));
            setSuccessMessage('Profile updated successfully!');
        } catch (error) {
            setErrorMessage(error.message || 'An error occurred. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleChangePassword = async (e) => {
        e.preventDefault();
        setPasswordSuccessMessage('');
        setPasswordErrorMessage('');

        try {
            const userId = user.id;
            await authServices.changePassword(userId, currentPassword, newPassword);
            setPasswordSuccessMessage('Password changed successfully!');
            setCurrentPassword('');
            setNewPassword('');
        } catch (error) {
            setPasswordErrorMessage(error.message || 'Failed to change password.');
        }
    };

    return (
        <div>
            <h2>Profile</h2>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>First Name:</label>
                    <input 
                        type="text" 
                        name="coor_fname" 
                        value={formData.coor_fname} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Last Name:</label>
                    <input 
                        type="text" 
                        name="coor_lname" 
                        value={formData.coor_lname} 
                        onChange={handleChange} 
                    />
                </div>
                <div>
                    <label>Username:</label>
                    <input 
                        type="text" 
                        name="coor_username" 
                        value={formData.coor_username} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input 
                        type="email" 
                        name="coor_email" 
                        value={formData.coor_email} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Contact:</label>
                    <input 
                        type="text" 
                        name="coor_contact" 
                        value={formData.coor_contact} 
                        onChange={handleChange} 
                    />
                </div>
                <div>
                    <label>Address:</label>
                    <textarea 
                        name="coor_address" 
                        value={formData.coor_address} 
                        onChange={handleChange} 
                    />
                </div>
                {user.coor_role === 'Admin' && (
                    <div>
                        <label>Role:</label>
                        <select 
                            name="coor_role" 
                            value={formData.coor_role} 
                            onChange={handleChange}
                        >
                            <option value="Admin">Admin</option>
                            <option value="Coordinator">Coordinator</option>
                        </select>
                    </div>
                )}
                <button type="submit" disabled={loading}>
                    {loading ? 'Updating...' : 'Update Profile'}
                </button>
            </form>

            <h3>Change Password</h3>
            {passwordErrorMessage && <p style={{ color: 'red' }}>{passwordErrorMessage}</p>}
            {passwordSuccessMessage && <p style={{ color: 'green' }}>{passwordSuccessMessage}</p>}
            <form onSubmit={handleChangePassword}>
                <div>
                    <label>Current Password:</label>
                    <input 
                        type="password" 
                        value={currentPassword} 
                        onChange={(e) => setCurrentPassword(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>New Password:</label>
                    <input 
                        type="password" 
                        value={newPassword} 
                        onChange={(e) => setNewPassword(e.target.value)} 
                        required 
                    />
                </div>
                <button type="submit">
                    Change Password
                </button>
            </form>
        </div>
    );
};

export default Profile;
