import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authServices from '../services/authServices';

const Login = () => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    // Clear input fields and messages when the component unmounts
    useEffect(() => {
        return () => {
            setIdentifier('');
            setPassword('');
            setError(null);
            setSuccess(null);
        };
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        console.log("Attempting login with:", { identifier, password }); // Log input values
    
        try {
            const userData = await authServices.login(identifier, password);
            console.log("User data received:", userData); // Log user data
            setSuccess('Login successful! Redirecting...');
            setError(null);
            localStorage.setItem('user', JSON.stringify(userData));
            
            setTimeout(() => {
                navigate('/dashboard');
                window.location.reload(); // Optional: Consider removing reload
            }, 2000);
        } catch (error) {
            console.error('Error during login:', error);
            setError(error.message || 'An unexpected error occurred.');
            setSuccess(null);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
            <form onSubmit={handleLogin}>
                <div>
                    <label htmlFor="identifier">Email or Username</label>
                    <input
                        id="identifier"
                        type="text"
                        placeholder="Email or Username"
                        value={identifier}
                        onChange={(e) => setIdentifier(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="password">Password</label>
                    <input
                        id="password"
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" disabled={loading}>
                    {loading ? 'Logging In...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
