// src/components/Profile.jsx
import React, { useEffect, useState } from 'react';
import { getCurrentUser, updateProfile } from '../services/authServices';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const user = getCurrentUser();
  const [profileData, setProfileData] = useState({
    username: '',
    email: '',
    role: '',
  });
  
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Initialize profile data from user
  useEffect(() => {
    if (user && user.coordinator) {
      const { username, email, role } = user.coordinator;
      setProfileData({ username, email, role });
    } else {
      navigate('/login'); // Redirect to login if no user is found
    }
  }, [user, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfileData({ ...profileData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedData = await updateProfile(user.coordinator.id, profileData);
      console.log('Profile updated successfully:', updatedData);
      alert('Profile updated successfully!');
    } catch (err) {
      setError(err.message || 'Failed to update profile.');
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
            value={profileData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={profileData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Role:</label>
          <select
            name="role"
            value={profileData.role}
            onChange={handleChange}
            disabled={user.coordinator.role !== 'Admin'} // Disable if not admin
          >
            <option value="Admin">Admin</option>
            <option value="Coordinator">Coordinator</option>
          </select>
        </div>
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default Profile;
