import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import authServices from '../services/authServices';

const UserProfile = () => {
  const { user } = useAuth(); // Get the current user from AuthContext
  const [profileData, setProfileData] = useState({
    coor_fname: '',
    coor_lname: '',
    email: '',
    role: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    // Fetch user profile data
    const fetchProfileData = async () => {
      try {
        const data = await authServices.getProfile(user.id); // Fetch profile using the service
        setProfileData(data); // Set the fetched profile data
      } catch (err) {
        setError('Failed to fetch profile data');
      }
    };

    fetchProfileData();
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await authServices.updateProfile(user.id, profileData); // Update profile using the service
      setSuccess('Profile updated successfully!');
      setError('');
    } catch (err) {
      setError('Failed to update profile');
      setSuccess('');
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="coor_fname"
          value={profileData.coor_fname}
          onChange={handleChange}
          placeholder="First Name"
        />
        <input
          type="text"
          name="coor_lname"
          value={profileData.coor_lname}
          onChange={handleChange}
          placeholder="Last Name"
        />
        <input
          type="email"
          name="email"
          value={profileData.email}
          onChange={handleChange}
          placeholder="Email"
        />
        {/* Role field with conditional rendering */}
        <div>
          <label>Role:</label>
          {user.role === 'admin' ? (
            <select name="role" value={profileData.role} onChange={handleChange}>
              <option value="admin">Admin</option>
              <option value="user">User</option>
              {/* Add more roles as needed */}
            </select>
          ) : (
            <p>{profileData.role}</p> // Display role as text for non-admin users
          )}
        </div>
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default UserProfile;
