// src/components/ManageUsers.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ManageUsers = () => {
    // State to hold the list of users
    const [users, setUsers] = useState([]);

    // Simulate fetching users (You can replace this with a real API call)
    useEffect(() => {
        const fetchUsers = async () => {
            // Replace this with your actual API request to get users
            const response = await fetch('/api/users'); // Example API endpoint
            const data = await response.json();
            setUsers(data);
        };
        
        fetchUsers();
    }, []);

    // Handle deleting a user
    const handleDelete = (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            // Simulate deleting a user (replace with actual API call)
            setUsers(users.filter(user => user.id !== userId));
        }
    };

    return (
        <div>
            <h1>Manage Users</h1>
            
            {/* User list as a table */}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.length > 0 ? (
                        users.map(user => (
                            <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>
                                    <Link to={`/edit-user/${user.id}`}>Edit</Link> |{' '}
                                    <button onClick={() => handleDelete(user.id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No users found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ManageUsers;
