import axios from 'axios';
import { useAuth } from '../context/AuthContext';

// Set the base URL for the API
const API_URL = 'http://localhost:1337/api'; // Replace with your actual API URL

const getAllUsers = async () => {
    const { token } = useAuth(); // Get token from AuthContext

    if (!token) {
        throw new Error('User not authenticated');
    }

    const response = await axios.get(`${API_URL}/coordinators/users`, {
        headers: {
            Authorization: `Bearer ${token}` // Use the token here
        }
    });

    return response.data; // Return the user data
};

const updateUser = async (userId, userData) => {
    const token = localStorage.getItem('token');
    if (!token) {
        throw new Error('User not authenticated');
    }

    const response = await axios.put(`${API_URL}/users/${userId}`, userData, {
        headers: {
            Authorization: `Bearer ${token}` // Include token in the headers
        }
    });
    
    return response.data; // This should return the updated user object
};

const deleteUser = async (userId) => {
    const token = localStorage.getItem('token');
    if (!token) {
        throw new Error('User not authenticated');
    }

    await axios.delete(`${API_URL}/users/${userId}`, {
        headers: {
            Authorization: `Bearer ${token}` // Include token in the headers
        }
    });
    
    // No return value for delete, just need to ensure it's successful
};

export default {
    getAllUsers,
    updateUser,
    deleteUser
};
